import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import styled from 'styled-components';
import GameBoard from './components/GameBoard';
import VirtualizedBoard from './components/VirtualizedBoard';
import GameControls from './components/GameControls';

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  min-height: 100vh;
  background-color: #1a1a1a;
  color: white;
  font-family: Arial, sans-serif;
`;

const Header = styled.h1`
  margin: 20px 0;
  color: #4CAF50;
`;

const GameContainer = styled.div`
  display: flex;
  gap: 20px;
  align-items: flex-start;
  max-width: 100vw;
  overflow: hidden;
`;

function App() {
  const [socket, setSocket] = useState(null);
  const [gameState, setGameState] = useState(null);
  const [gameId, setGameId] = useState('');
  const [playerName, setPlayerName] = useState('');
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const newSocket = io('http://localhost:5001');
    setSocket(newSocket);

    newSocket.on('connect', () => {
      setConnected(true);
    });

    newSocket.on('disconnect', () => {
      setConnected(false);
    });

    newSocket.on('gameState', (state) => {
      setGameState(state);
    });

    newSocket.on('gameOver', (winner) => {
      alert(`ゲーム終了！勝者: ${winner ? winner.playerName : '引き分け'}`);
    });

    newSocket.on('error', (error) => {
      alert(`エラー: ${error}`);
    });

    return () => newSocket.close();
  }, []);

  const joinGame = () => {
    if (socket && gameId && playerName) {
      socket.emit('joinGame', { gameId, playerName });
    }
  };

  const makeMove = (row, col) => {
    if (socket && gameState) {
      socket.emit('makeMove', { gameId: gameState.gameId, row, col });
    }
  };

  return (
    <AppContainer>
      <Header>巨大オセロ - Huge Reversi</Header>

      {!gameState ? (
        <GameControls
          gameId={gameId}
          setGameId={setGameId}
          playerName={playerName}
          setPlayerName={setPlayerName}
          onJoinGame={joinGame}
          connected={connected}
        />
      ) : (
        <GameContainer>
          {gameState && gameState.boardSize > 16 ? (
            <VirtualizedBoard 
              gameState={gameState} 
              onMakeMove={makeMove}
              validMoves={new Set()}
            />
          ) : (
            <GameBoard 
              gameState={gameState} 
              onMakeMove={makeMove}
            />
          )}
        </GameContainer>
      )}
    </AppContainer>
  );
}

export default App;
